CC
CC    CONVERSION SUBROUTINE FOR LIST DATA
CC
      SUBROUTINE LSTCNV(N,NLST,LLST,LCNV,IERR,IUT0)
C
      IMPLICIT NONE
      INTEGER*4 N,NLST
      INTEGER*4 LLST(NLST),LCNV(N)
      INTEGER*4 IERR,IUT0
C
      INTEGER*4 ILST,I
C
      DO 1000 ILST=1,NLST
         I=LLST(ILST)
         IF (I.LT.1.OR.I.GT.N) GOTO 9999
         LLST(ILST)=LCNV(I)
 1000 CONTINUE
C
      RETURN
C
 9999 CONTINUE
      WRITE(IUT0,*)  ' ** LSTCNV: INVALID NUMBER IN LSTCNV ***'
      IERR=1
      RETURN
C
      END
C
CC
CC    CONVERSION SUBROUTINE FOR INTEGER TYPE DATA
CC
      SUBROUTINE INTCNV(N,NR,IDATA,LCNV,IWORK,IERR,IUT0)
C
      IMPLICIT NONE
      INTEGER*4 N,NR
      INTEGER*4 IDATA(NR),IWORK(NR)
      INTEGER*4 LCNV(NR)
      INTEGER*4 IERR,IUT0
C
      INTEGER*4 I,IR
C
      DO 1000 I=1,N
         IWORK(I)=IDATA(I)
 1000 CONTINUE
C
      DO 2000 IR=1,NR
         I=LCNV(IR)
         IF(I.LT.1.OR.I.GT.N) GOTO 9999
         IDATA(I)=IWORK(IR)
 2000 CONTINUE
C
      RETURN
C
 9999 CONTINUE
      WRITE(IUT0,*)  ' ** INTCNV: INVALID NUMBER IN INTCNV ***'
      IERR=1
      RETURN
C
      END
C
