CC
CC    CONVERSION SUBROUTINE FOR INTEGER TYPE DATA
CC
      SUBROUTINE NODCNV(NP,NE,N2,NODE,LPCNV,LECNV,NODWK1,IERR,IUT0)
C
      IMPLICIT NONE
      INTEGER*4 NP,NE,N2
      INTEGER*4 NODE(N2,NE),LPCNV(NP),LECNV(NE)
      INTEGER*4 NODWK1(N2,NE)
      INTEGER*4 IERR,IUT0
C
      INTEGER*4 I,IE,IP,IEC,IPC
C
      DO 1000 IE=1,NE
         DO 1100 I=1,8
            NODWK1(I,IE)=0
 1100    CONTINUE
 1000 CONTINUE
C
      DO 2000 IE=1,NE
         IEC=LECNV(IE)
         IF(IEC.LT.1.OR.IEC.GT.NE) GOTO 9999
         DO 2100 I=1,8
            IP=NODE(I,IE)
            IF (IP.EQ.0) GOTO 2100
            IPC=LPCNV(IP)
            IF(IPC.LT.1.OR.IPC.GT.NP) GOTO 9999
            NODWK1(I,IEC)=IPC
 2100    CONTINUE
 2000 CONTINUE
C
      DO 3000 IE=1,NE
         DO 3100 I=1,8
            NODE(I,IE)=NODWK1(I,IE)
 3100    CONTINUE
 3000 CONTINUE
C
      RETURN
C
 9999 CONTINUE
      WRITE(IUT0,*)  ' ** NODCNV: INVALID NUMBER IN NODCNV ***'
      IERR=1
      RETURN
C
      END
C
CC
CC    CONVERSION SUBROUTINE FOR REFINED ATTRIBUTE DATA
CC
      SUBROUTINE ATRCNV(NE0,NE,LEATTR,LEACNV,LWORK,IERR,IUT0,IUT6)
C
      IMPLICIT NONE
      INTEGER*4 NE0,NE
      INTEGER*4 LEATTR(NE),LEACNV(NE),LWORK(NE)
      INTEGER*4 IERR,IUT0,IUT6
C
      INTEGER*4 IE0,IE
C
      DO 1000 IE0=1,NE0
          LWORK(IE0)=LEATTR(IE0)
 1000 CONTINUE
C
      DO 2000 IE=1,NE
          IE0=LEACNV(IE)
          IF(IE0.LT.1.OR.IE0.GT.NE0) GOTO 9999
          LEATTR(IE)=LWORK(IE0)
 2000 CONTINUE
C
      RETURN
C
 9999 CONTINUE
      WRITE(IUT0,*)  ' ** ATRCONV: INVALID ELM. NUM. IN LEACNV ***'
      IERR=1
      RETURN
C
      END
C=======================================================================
      SUBROUTINE CRSCVI(NP,NPPMAX,NCRS,NCRS2,NPP,IPCRS,ITPCRS)
C=======================================================================
      IMPLICIT NONE
C
      INTEGER NP,NPPMAX,NCRS,NCRS2
      INTEGER NPP(NP),IPCRS(NCRS),ITPCRS(NCRS2)
      INTEGER ITCRS,ICRS,IP,K
C
      CALL USTSTA(74)
C
      ITCRS=0
      ICRS =0
      DO 110 IP=1,NP
         DO 120 K=1,NPPMAX
            ITCRS=ITCRS+1
            IF (K.GT.NPP(IP)) THEN
               ITPCRS(ITCRS)=0
            ELSE
               ICRS=ICRS+1
               ITPCRS(ITCRS)=IPCRS(ICRS)
            ENDIF
 120     CONTINUE
 110  CONTINUE
C
      CALL USTEND(74)
C
      RETURN
      END
C=======================================================================
!     SUBROUTINE CRSCVA(NP,NPPMAX,NCRS,NCRS2,NPP,A,TA)
      SUBROUTINE CRSCVA(NP,NPPMAX,NCRS,NCRS2,NPP,A,TA,ICRS_T)
C=======================================================================
      IMPLICIT NONE
C
      INTEGER NP,NPPMAX,NCRS,NCRS2
      INTEGER NPP(NP),IPCRS(NCRS)
      REAL*4  A(NCRS),TA(NCRS2)
      INTEGER ITCRS,ICRS,IP,K, ICRS_T(NP)
      
C
      ITCRS=0
      ICRS =0
!ocl norecurrence      
      DO 110 IP=1,NP
         ICRS =ICRS_T(IP)
         DO 120 K=1,NPP(IP)
            ITCRS=NPPMAX*(IP-1)+K
            TA(ITCRS)=A(ICRS+K)
 120     CONTINUE
         DO 130 K=NPP(IP)+1,NPPMAX
            ITCRS=NPPMAX*(IP-1)+K
            TA(ITCRS)=0.0E0
 130     CONTINUE
 110  CONTINUE
C
      RETURN
      END
